import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Alert, Button, Card, CardBody, CardText, CardTitle, Col, Container, Form, FormGroup, Input, Label, Row } from "reactstrap";



function Signup(){

    const [data,setData]=useState({
        name :'',
        sid : '',
        password :''
    });
   

    

    const handleChange=(event,property)=>{
        setData({...data,[property]:event.target.value})

    }
    return(
   <>
   <Container>
    <Row className="mt-5">
    <Col sm={{size:6,offset:3}}>
        <Card>
            <CardBody>
                <CardTitle>Create Account</CardTitle>
                <CardText>
                    <Form onSubmit={handleSignup}>
                        <FormGroup>

                             {/* Name Section */}
                            <Label for="Name">
                                Name
                             </Label>
                                <Input
                                id="Name"
                                placeholder="Enter your Name "
                                onChange={(e)=>handleChange(e,'name')}
                                value={data.name}
                                />

                             {/* School Id Section */  }
                             <Label for="SchoolId">
                                School Id
                             </Label>
                             <Input
                                id="sid"
                                placeholder="Enter the Scool Id"
                                onChange={(e)=>handleChange(e,'sid')}
                                value={data.sid}
                             />

                             {/* Password Section */}
                             <Label for="Password">
                                Password
                             </Label>
                             <Input
                                id="password"
                                placeholder="Enter your Password"
                                onChange={(e)=>handleChange(e,'password')}
                                value={data.password}
                             />


                        </FormGroup>
                            <Button color="danger">
                            Signup

                            </Button>
                    </Form>

                </CardText>
                
            </CardBody>
        </Card>
    </Col>
        
    </Row>
   </Container>
   </>   
  

       
    )
}
export default Signup;
