import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Toast,ToastBody,Input,Button, Container,Row,Col,Card,CardBody,CardTitle,CardText,Form,FormGroup,Label, ToastHeader } from "reactstrap";
import axios from "axios";
import { setToken } from "../service/JwtToken";

function Login ()
{
    const[data,setData]=useState({
        schoolId : '',
        password :''
    }
    );
    const handleChange=(event,property)=>{
        setData({...data,[property ]: event.target.value})
    
    };
    
    const handleLogin=async(data)=>{
        try {
            const response=await axios.post('http://localhost:8080/auth/login',data);
            console.log('generating token');
            const token =response.data;
            console.log(token)
            console.log("Set token");
            setToken(token);
            <div className="p-3 my-2 rounded">
              <Toast>
            <ToastHeader>
                SuccessFull
            </ToastHeader>
                   <ToastBody>
                   Done 
                </ToastBody>
            </Toast>
            </div>
           
            
    
        } catch (error) {
            <div className="p-3 my-2 rounded">
              <Toast>
            <ToastHeader>
                UnSuccessFull
            </ToastHeader>
                   <ToastBody>
                   Fail
                </ToastBody>
            </Toast>
            </div>
            
        }
     
        
    };
    
    return(
        <>
            <Container>
                <Row className="mt-5">
                    <Col sm={{size:6,offset:3}}>
                      <Card>
                            <CardBody>
                                <CardTitle>
                                    Create Account
                                </CardTitle>
                                        <CardText>
                                            <Form onSubmit={handleLogin}>
                                                 {/* School Id section*/ }
                                                 <FormGroup>
                                                         <Label for="School Id">
                                                              School Id
                                                         </Label>
                                                        <Input
                                                        id="schoolId"
                                                        placeholder="Enter your School Id"
                                                        onChange={(e)=>handleChange(e,'schoolId')}
                                                        value={data.schoolId}
                                                        />
                                                </FormGroup>

                                                {/* Password Section  */}
                                                 <FormGroup>
                                                         <Label for="password">
                                                              Password
                                                         </Label>
                                                        <Input
                                                        id="password"
                                                        type="password"
                                                        placeholder="Enter your Password "
                                                        onChange={(e)=>handleChange(e,'password')}
                                                        value={data.password}
                                                        />
                                                </FormGroup>

                                                <Button color="danger" type="submit">
                                                 Login
                                                 </Button>
                                           </Form>
                                     </CardText>     
                            </CardBody>
                     </Card>
                 </Col>
               </Row>
         </Container>
        </>
    )
   
}
export default Login;
