import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Input,Button, Container,Row,Col,Card,CardBody,CardTitle,CardText,Form,FormGroup,Label, Alert } from "reactstrap";
import { login } from "../service/Authservice";
import 'react-toastify/dist/ReactToastify.css';
import { toast } from "react-toastify";

function Login ()
{
    const[data,setData]=useState({
        schoolId : '',
        password :''
    }
    );
    const handleChange=(event,property)=>{
        setData({...data,[property ]: event.target.value})
    
    };
    const valid =()=>{
        if (data.schoolId ==='' && data.password ==='') {
            return false;
            
        } else {
            return true;
        }
    };
    
    const handleLogin=async(event)=>{
        event.preventDefault();
        if(valid)
        {
            const status=login(data);
            alert(status);
            if(login(data)==200 || login(data)==201)
            {
                alert('Done');
                toast.error('success');
            }
            if(login(data)==404)
            {
                toast.error("No User Found");
            }

            else
            {
                toast.error("Incorrect Password or Email");

            }
        }
        else
        {
            toast.error("Enter the fields");

        }     
        
    };
    
    return(
        <>
            <Container>
                <Row className="mt-5">
                    <Col sm={{size:6,offset:3}}>
                      <Card>
                            <CardBody>
                                <CardTitle>
                                    Create Account
                                </CardTitle>
                                        <CardText>
                                            <Form onSubmit={handleLogin}>
                                                 {/* School Id section*/ }
                                                 <FormGroup>
                                                         <Label for="School Id">
                                                              School Id
                                                         </Label>
                                                        <Input
                                                        id="schoolId"
                                                        placeholder="Enter your School Id"
                                                        onChange={(e)=>handleChange(e,'schoolId')}
                                                        value={data.schoolId}
                                                        />
                                                </FormGroup>

                                                {/* Password Section  */}
                                                 <FormGroup>
                                                         <Label for="password">
                                                              Password
                                                         </Label>
                                                        <Input
                                                        id="password"
                                                        type="password"
                                                        placeholder="Enter your Password "
                                                        onChange={(e)=>handleChange(e,'password')}
                                                        value={data.password}
                                                        />
                                                </FormGroup>

                                                <Button color="danger" type="submit">
                                                 Login
                                                 </Button>
                                           </Form>
                                     </CardText>     
                            </CardBody>
                     </Card>
                 </Col>
               </Row>
         </Container>
        </>
    )
   
}
export default Login;
